function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),s=document.querySelector("body"),c=document.querySelector(".nav__btns");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked"),c.classList.remove("active")):(t.classList.add("active"),e.classList.add("active-burger"),s.classList.add("locked"),c.classList.add("active"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked"),c.classList.remove("active"))}))}function submenu(){const e=document.querySelector(".submenu"),t=document.querySelector(".with__submenu"),s=document.querySelector(".trigger__link"),c=document.querySelector(".submenu__arrow");t.addEventListener("click",(()=>{e.classList.toggle("active"),s.classList.toggle("active"),c.classList.toggle("active")}))}burgerMenu(),submenu();